//     for (let j = 0; j < row.length; j++) {
//       computerBoardComponent.push(
//         <ComputerSquare
//           symbol={computerBoardState[i][j]}
//           onClick={setComputerBoard}
//           boardState={computerBoardState}
//           x={i}
//           y={j}
//         />
//       );
//       if (computerBoardState[i][j] === "X") {
//         humanCount++;
//       }
//     }
//   }

  let humanBoard = randomlyChooseFiveShips([
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ]);
  const [humanBoardState, setHumanBoard] = useState(humanBoard);

  const humanBoardComponent = [];
  let computerCount = 0;

  for (let i = 0; i < humanBoardState.length; i++) {
    let row = humanBoardState[i];
    for (let j = 0; j < row.length; j++) {
      humanBoardComponent.push(
        <HumanSquanre
          symbol={humanBoardState[i][j]}
          onClick={setHumanBoard}
          boardState={humanBoardState}
          x={i}
          y={j}
        />
      );
      if (humanBoardState[i][j] === "X") {
        computerCount++;
      }
    }
  }

  return (
    <div>
      <div class="count">
        Human: {humanCount}{" "}
        <span class="computerCount">Computer: {computerCount}</span>
      </div>

      <div class={humanDisplay}>
        <div class="turn">Human's Turn</div>
        <div id="board">{computerBoardComponent}</div>
      </div>

      <div class={computerDisplay}>
        <div class="turn">Computer's Turn</div>
        <div id="board">{humanBoardComponent}</div>
      </div>
    </div>
  );
}

function randomlyChooseFiveShips(board) {
  let newArray = [];
  for (let i = 0; i < board.length; i++) {
    newArray.push(i);
  }

  let shipCount = 0;
  while (shipCount != totalShip) {
    let rowPos = newArray[Math.floor(Math.random() * newArray.length)];
    let colPos = newArray[Math.floor(Math.random() * newArray.length)];
    if (board[rowPos][colPos] === "") {
      board[rowPos][colPos] = "Ship";
      shipCount += 1;
    }
  }
  return board;
}
