import ComputerSquare from "./ComputerSquare";
import HumanSquare from "./HumanSquare";
import "./Board.css";
import { useSelector } from "react-redux";
import ResetButton from "./ResetButton";

export default function Board() {
  const computerBoardState = useSelector((state) => state.game.gameBoard);
  const clickCount = useSelector((state) => state.game.count);

  const huamBoardState = useSelector((state) => state.computerGame.gameBoard);
  const computerClickCount = useSelector((state) => state.computerGame.count);

  let turn = useSelector((state) => state.turn);

  const computerBoardComponent = [];
  const humanBoardComponent = [];
  const totalShips = 5;
  let winner = "";

  for (let i = 0; i < computerBoardState.length; i++) {
    let row = computerBoardState[i];
    for (let j = 0; j < row.length; j++) {
      computerBoardComponent.push(
        <ComputerSquare symbol={computerBoardState[i][j]} x={i} y={j} />
      );
    }
  }

  for (let i = 0; i < huamBoardState.length; i++) {
    let row = huamBoardState[i];
    for (let j = 0; j < row.length; j++) {
      humanBoardComponent.push(
        <HumanSquare symbol={huamBoardState[i][j]} x={i} y={j} />
      );
    }
  }

  //   if (clickCount === totalShips) {
  //     winner = "Game Over! You Won!";
  //   }

  while (clickCount != totalShips && computerClickCount != totalShips) {
    if (turn === 0) {
      return (
        <div>
          <div class="count">Score: {clickCount} </div>
          <div class="count">Human's Turn </div>
          <div class="winner">{winner} </div>
          <div id="board">{computerBoardComponent}</div>
          <ResetButton text="Reset The Game" />
        </div>
      );
    } else {
      return (
        <div>
          <div class="count">Score: {computerClickCount} </div>
          <div class="count">Computer's Turn </div>
          <div class="winner">{winner} </div>
          <div id="board">{humanBoardComponent}</div>
          <ResetButton text="Reset The Game" />
        </div>
      );
    }
  }
  if (clickCount === totalShips) {
    return (
      <div>
        <div class="count">Score: {clickCount} </div>
        <div class="count">Human's Turn </div>
        <div class="winner">{winner} </div>
        <div id="board">{computerBoardComponent}</div>
        <ResetButton text="Reset The Game" />
      </div>
    );
  }
}
