import Square from "./Square";
import "./Board.css";
import { useState } from "react";

const totalShip = 5;

export default function Board() {
  let board = randomlyChooseFiveShips([
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ]);
  const [boardState, setBoard] = useState(board);

  const boardComponent = [];
  let count = 0;

  for (let i = 0; i < boardState.length; i++) {
    let row = boardState[i];
    for (let j = 0; j < row.length; j++) {
      boardComponent.push(
        <Square
          symbol={boardState[i][j]}
          onClick={setBoard}
          boardState={boardState}
          x={i}
          y={j}
        />
      );
      if (boardState[i][j] === "X") {
        count++;
      }
    }
  }

  return (
    <div>
      <div class="count">Player Score: {count}</div>
      <div id="board">{boardComponent}</div>
    </div>
  );
}

function randomlyChooseFiveShips(board) {
  let newArray = [];
  for (let i = 0; i < board.length; i++) {
    newArray.push(i);
  }
  let shipPosition = [];
  while (shipPosition.size <= totalShip) {
    let rowPos = newArray[Math.floor(Math.random() * newArray.length)];
    let colPos = newArray[Math.floor(Math.random() * newArray.length)];
    if (shipPosition.indexOf[rowPos, colPos])
    shipPosition.push([rowPos, colPos]);
  }
  shipPosition.forEach(function (pos) {
    board[pos[0]][pos[1]] = "Ship";
  });
  return board;
}
