import { cloneDeep } from 'lodash';


const initialBoard = [
  ["", "", ""],
  ["", "", ""],
  ["", "", ""],
]

let defaultState = {
  count: 0,
  gameBoard: randomlyChooseFiveShips(initialBoard)
}

export default function gameReducer(state, action) {


    if (state === undefined) {
        return defaultState;
    }
    if (action.type === "computerBoardClick") {
        const value = state.gameBoard[action.x][action.y]
        if (value === "Ship") {
            state.gameBoard[action.x][action.y] = "X"
            state.count++
        } else if (value === "") {
            // TODO: There is a library called immutable for React. Try it.
            const copy = cloneDeep(state.gameBoard);
            copy[action.x][action.y] = "✓";
            state.gameBoard = copy;
        }
        return {...state}
    }
    if (action.type === 'RESET') {
        state.gameBoard = defaultState.gameBoard
        state.count = 0
        return {...state};
    }
    return state;
}

function randomlyChooseFiveShips(board) {
    const totalShip = 5;
    let newArray = [];
    for (let i = 0; i < board.length; i++) {
      newArray.push(i);
    }

    let shipCount = 0;
    while (shipCount !== totalShip) {
      let rowPos = newArray[Math.floor(Math.random() * newArray.length)];
      let colPos = newArray[Math.floor(Math.random() * newArray.length)];
      if (board[rowPos][colPos] === "") {
        board[rowPos][colPos] = "Ship";
        shipCount += 1;
      }
    }
    return board;
  }
