import "./ComputerSquare.css";
import { useDispatch } from "react-redux";
import { BoardClickCountContext } from "./BoardClickCountProvider";

export default function ComputerSquare(props) {
  const [globalCountState, globalCountDispatch] = useContext(
    BoardClickCountContext
  );

  const symbol = props.symbol;

  let backgroundColor = "blueColor";
  if (symbol === "X") {
    backgroundColor = "redColor";
  } else if (symbol === "✓") {
    backgroundColor = "greenColor";
  }

  const dispatch = useDispatch();

  return (
    <div
      onClick={() => {
        dispatch({
          type: "boardClick",
          x: props.x,
          y: props.y,
        });
        globalCountDispatch({
          type: "boardClick",
          symbol,
        });
      }}
      id="square"
      class={borderColor}
    >
      {symbol}
    </div>
  );
}


