import "./Game.css";
import { useSelector } from "react-redux";
import ResetButton from "./ResetButton";
import HumanBoard from "./HumanBoard";
import ComputerBoard from "./ComputerBoard";

export default function Game() {
  const humanClickCount = useSelector((state) => state.game.count);
  const computerClickCount = useSelector((state) => state.computerGame.count);
  const turn = useSelector((state) => state.turn);

  const computerBoardState = useSelector(
    (state) => state.computerGame.gameBoard
  );

  const computerBoardComponent = [];
  for (let i = 0; i < computerBoardState.length; i++) {
    let row = computerBoardState[i];
    for (let j = 0; j < row.length; j++) {
      computerBoardComponent.push(
        <ComputerSquare symbol={computerBoardState[i][j]} x={i} y={j} />
      );
    }
  }

  return <div class="board">{computerBoardComponent}</div>;

  const totalShips = 5;
  let winner = "";
  if (humanClickCount === totalShips) {
    winner = "Game Over! You Won!";
  } else if (computerClickCount === totalShips) {
    winner = "Game Over! Computer Won!";
  }
  return (
    <div>
      {/* <div class="count">Score: {humanClickCount} </div> */}
      {/* <div class="count">{turn === 0 ? "Human's Turn" : "Computer's Turn"}</div> */}
      <div class="winner">{winner} </div>
      {/* {turn === 0 ? <ComputerBoard /> : <HumanBoard />} */}
      <div class="count">Human Score: {humanClickCount} </div>
      {<ComputerBoard />}
      <ResetButton text="Reset The Game" />
    </div>
  );
}
