import { cloneDeep } from "lodash";
import { randomlyChooseFiveShips } from "./reducerUtils";

export default function computerBoardReducer(state, action) {
  const initialBoard = [
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ];

  const initialState = {
    count: 0,
    board: randomlyChooseFiveShips(initialBoard),
  };

  if (state === undefined) {
    return defaultState;
}

  switch (action.type) {
    case "HumanClickSquare": {
      const value = state.board[action.x][action.y];
      if (value === "Ship") {
        state.board[action.x][action.y] = "X";
        state.count++;
      } else if (value === "") {
        const copy = cloneDeep(state.board);
        copy[action.x][action.y] = "⚫";
        state.board = copy;
      }
      return { ...state };
    }
    case "RESET": {
      // const newState = {
      //   count: 0,
      //   board: randomlyChooseFiveShips(initialBoard),
      // };
      // state = newState;
      // return { ...state };
      state.board = initialState.board;
      state.count = 0;
      return { ...state };

      // state.board = [
      //   ["", "", ""],
      //   ["", "", ""],
      //   ["", "", ""],
      // ];
      // state.count = 0;
      // return { ...state };
    }
    default:
      return state;
  }
}
