      //     onClick={setComputerBoard}
      //     boardState={computerBoardState}
      //     x={i}
      //     y={j}
      //   />
      // );
      if (computerBoardState[i][j] === "X") {
        humanCount++;
      }
    }
  }

  return (
    <div>
      <div class="count">Human: {humanCount} </div>
    </div>
  );
}

