import { cloneDeep } from "lodash";
import { randomlyChooseFiveShips } from "./reducerUtils";

const initialBoard = [
  ["", "", ""],
  ["", "", ""],
  ["", "", ""],
];

const initialState = {
  count: 0,
  board: randomlyChooseFiveShips(initialBoard),
};

export default function humanBoardReducer(state = initialState, action) {
  switch (action.type) {
    case "HumanClickSquare": {
      setTimeout(() => {});
      const value = state.board[action.x][action.y];
      if (value === "Ship") {
        state.board[action.x][action.y] = "X";
        state.count++;
      } else if (value === "") {
        // TODO: There is a library called immutable for React. Try it.
        const copy = cloneDeep(state.board);
        copy[action.x][action.y] = "✓";
        state.board = copy;
      }
      return { ...state };
    }
    case "": {
      setTimeout(() => {
        turnDispatch({
          type: "ComputerClickSquare",
          x: Math.floor(Math.random() * humanBoardState.length),
          y: Math.floor(Math.random() * humanBoardState[0].length),
        });
      }, 2000);
    }
    default:
      return state;
  }
}
