import Square from "./Square";
import "./Board.css";
import { useState } from "react";

const totalShip = 5;

export default function Board() {
  const [playerBoardState, setPlayerBoard] = useState([
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ]);

  //   setPlayerBoard(randomlyChooseFiveShips(playerBoardState));
  const playerBoardComponent = [];
  let count = 0;

  for (let i = 0; i < playerBoardState.length; i++) {
    for (let j = 0; j < playerBoardState[i]; j++) {
      playerBoardComponent.push(
        <Square
          symbol={playerBoardState[i][j]}
          onClick={setPlayerBoard}
          playerBoardState={playerBoardState}
          x={i}
          y={j}
        />
      );
      if (playerBoardState[i][j] === "X") {
        count++;
      }
    }
  }
  return (
    <div>
      <div class="count">Player Score: {count}</div>
      <div id="board">{playerBoardComponent}</div>
    </div>
  );
}

function randomlyChooseFiveShips(board) {
  let newArray = [];
  for (let i = 0; i < board.length; i++) {
    newArray.push(i);
  }
  let shipPosition = new Set();
  while (shipPosition.size != totalShip) {
    let rowPos = newArray[Math.floor(Math.random() * newArray.length)];
    let colPos = newArray[Math.floor(Math.random() * newArray.length)];
    shipPosition.add([rowPos, colPos]);
  }
  shipPosition.forEach(function (pos) {
    board[pos[0]][pos[1]] = "Ship";
  });
  return board;
}
