import ComputerSquare from "./ComputerSquare";
import HumanSquanre from "./HumanSquare";
import "./Board.css";
import { useState } from "react";

const totalShip = 5;
// let noneDisplay = "noneDisplay";
// let display = "display";
// let computerDisplay = display;
// let humanDisplay = noneDisplay;
let turn = 0;
let humanCount = 0;
let computerCount = 0;

export default function Board() {
  while (humanCount != totalShip && computerCount != totalShip) {
    if (turn === 0) {
      let computerBoard = randomlyChooseFiveShips([
        ["", "", ""],
        ["", "", ""],
        ["", "", ""],
      ]);
      const [computerBoardState, setComputerBoard] = useState(computerBoard);

      const computerBoardComponent = [];

      for (let i = 0; i < computerBoardState.length; i++) {
        let row = computerBoardState[i];
        for (let j = 0; j < row.length; j++) {
          computerBoardComponent.push(
            <ComputerSquare
              symbol={computerBoardState[i][j]}
              onClick={setComputerBoard}
              boardState={computerBoardState}
              x={i}
              y={j}
            />
          );
          if (computerBoardState[i][j] === "X") {
            humanCount++;
          }
        }
      }
      turn = 1;
    } else {
      humanBoard();
      turn = 0;
    }
  }

  return (
    <div>
      <div class="count">
        Human: {5} <span class="computerCount">Computer: {5}</span>
      </div>

      {/* <div>
        <div class="turn">Human's Turn</div>
        <div id="board">{computerBoardComponent}</div>
      </div> */}

      <div>
        <div class="turn">Computer's Turn</div>
        <div id="board">{humanBoardComponent}</div>
      </div>
    </div>
  );
}

function computerBoard() {
  let computerBoard = randomlyChooseFiveShips([
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ]);
  const [computerBoardState, setComputerBoard] = useState(computerBoard);

  const computerBoardComponent = [];

  for (let i = 0; i < computerBoardState.length; i++) {
    let row = computerBoardState[i];
    for (let j = 0; j < row.length; j++) {
      computerBoardComponent.push(
        <ComputerSquare
          symbol={computerBoardState[i][j]}
          onClick={setComputerBoard}
          boardState={computerBoardState}
          x={i}
          y={j}
        />
      );
      if (computerBoardState[i][j] === "X") {
        humanCount++;
      }
    }
  }
}

function humanBoard() {
  let humanBoard = randomlyChooseFiveShips([
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ]);
  const [humanBoardState, setHumanBoard] = useState(humanBoard);

  const humanBoardComponent = [];

  for (let i = 0; i < humanBoardState.length; i++) {
    let row = humanBoardState[i];
    for (let j = 0; j < row.length; j++) {
      humanBoardComponent.push(
        <HumanSquanre
          symbol={humanBoardState[i][j]}
          onClick={setHumanBoard}
          boardState={humanBoardState}
          x={i}
          y={j}
        />
      );
      if (humanBoardState[i][j] === "X") {
        computerCount++;
      }
    }
  }
}

function randomlyChooseFiveShips(board) {
  let newArray = [];
  for (let i = 0; i < board.length; i++) {
    newArray.push(i);
  }

  let shipCount = 0;
  while (shipCount != totalShip) {
    let rowPos = newArray[Math.floor(Math.random() * newArray.length)];
    let colPos = newArray[Math.floor(Math.random() * newArray.length)];
    if (board[rowPos][colPos] === "") {
      board[rowPos][colPos] = "Ship";
      shipCount += 1;
    }
  }
  return board;
}
