import HumanSquare from "./HumanSquare";
import "./Game.css";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";

export default function HumanBoard() {
  const huamBoardState = useSelector((state) => state.compu.gameBoard);

  const humanBoardComponent = [];
  for (let i = 0; i < huamBoardState.length; i++) {
    let row = huamBoardState[i];
    for (let j = 0; j < row.length; j++) {
      humanBoardComponent.push(
        <HumanSquare symbol={huamBoardState[i][j]} x={i} y={j} />
      );
    }
  }

  const turnDispatch = useDispatch();
  setTimeout(() => {
    turnDispatch({ type: "ComputerClick" });
  }, 4000);
  return <div class="board">{humanBoardComponent}</div>;
}
