import Square from "./ComputerSquare";
import "./Board.css";
import { useState } from "react";

const totalShip = 5;
let playerTurn = "Human's turn";

export default function Board() {
  let computerBoard = randomlyChooseFiveShips([
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ]);
  const [computerBoardState, setBoard] = useState(computerBoard);

  const boardComponent = [];
  let humanCount = 0;

  for (let i = 0; i < boardState.length; i++) {
    let row = boardState[i];
    for (let j = 0; j < row.length; j++) {
      boardComponent.push(
        <Square
          symbol={boardState[i][j]}
          onClick={setBoard}
          boardState={boardState}
          x={i}
          y={j}
        />
      );
      if (boardState[i][j] === "X") {
        humanCount++;
      }
    }
  }

  return (
    <div>
      {/* <div>
        <span id="count">Human: {humanCount}</span>
        <span id="count">Computer: {}</span>
      </div> */}
      <div class="count">
        Human: {humanCount} <span class="computerCount">Computer: {}</span>
      </div>
      <div class="turn">{playerTurn}</div>
      <div id="board">{boardComponent}</div>
    </div>
  );
}

function randomlyChooseFiveShips(board) {
  let newArray = [];
  for (let i = 0; i < board.length; i++) {
    newArray.push(i);
  }

  let shipCount = 0;
  while (shipCount != totalShip) {
    let rowPos = newArray[Math.floor(Math.random() * newArray.length)];
    let colPos = newArray[Math.floor(Math.random() * newArray.length)];
    if (board[rowPos][colPos] === "") {
      board[rowPos][colPos] = "Ship";
      shipCount += 1;
    }
  }
  return board;
}
