export default function Square(props) {
  const symbol = props.symbol;
  let backgroundColor = "blueColor";
  if (symbol === "X") {
    backgroundColor = "redColor";
  } else if (symbol === "√") {
    backgroundColor = "greenColor";
  }
  return (
    <div
      onClick={() => {
        const { boardState, onClick, x, y } = props;
        if (symbol === "O") {
          boardState[x][y] = "√";
        }
        onClick([...boardState]);
      }}
      id="square"
      class={backgroundColor}
    ></div>
  );
}
