import { cloneDeep } from "lodash";

const initialBoard = [
  ["", "", ""],
  ["", "", ""],
  ["", "", ""],
];

const initState = {
  count: 0,
  gameBoard: randomlyChooseFiveShips(initialBoard),
};

export default function computerBoardReducer(state = initState, action) {
  switch (action.type) {
    case "HumanClickBoard": {
      const value = state.gameBoard[action.x][action.y];
      if (value === "Ship") {
        state.gameBoard[action.x][action.y] = "X";
        state.count++;
      } else if (value === "") {
        const copy = cloneDeep(state.gameBoard);
        copy[action.x][action.y] = "⚫";
        state.gameBoard = copy;
      }
      return { ...state };
    }
    case "RESET": {
      return {
        count: 0,
        gameBoard: randomlyChooseFiveShips(initialBoard),
      };
    }
    default:
      return state;
  }
}
