export default function gameReducer(state, action) {
    const initialBoard = [
        ["", "", ""],
        ["", "", ""],
        ["", "", ""],
      ]

    const defaultState = {
        count: 0,
        gameBoard: randomlyChooseFiveShips(initialBoard)
    }

    if (state === undefined) {
        return defaultState;
    }
    if (action.type === "boardClick") {
        const value = state.gameBoard[action.x][action.y]
        if (value === "Ship") {
            state.gameBoard[action.x][action.y] = "X"
            state.count++
        } else if (value === "") {
            state.gameBoard[action.x][action.y] = "✓"
            state.count++
        }

        return {...state}
    }
    if (action.type === 'RESET') {
        // const newBoard = randomlyChooseFiveShips(initialBoard)
        // for (let i = 0; i < state.gameBoard.length; i++) {
        //     for (let j = 0; j < state.gameBoard.length; j++){
        //         state.gameBoard[i][j] = newBoard[i][j];
        //     }
        // }
        // state.count = 0
        state.gameBoard = [
            ["", "", ""],
            ["", "", ""],
            ["", "", ""],
          ]
        defaultState = {
            count: 0,
            gameBoard: randomlyChooseFiveShips(initialBoard)
        }
        return {...state};
    }
    return state;

}

function randomlyChooseFiveShips(board) {
    const totalShip = 5;
    let newArray = [];
    for (let i = 0; i < board.length; i++) {
      newArray.push(i);
    }

    let shipCount = 0;
    while (shipCount !== totalShip) {
      let rowPos = newArray[Math.floor(Math.random() * newArray.length)];
      let colPos = newArray[Math.floor(Math.random() * newArray.length)];
      if (board[rowPos][colPos] === "") {
        board[rowPos][colPos] = "Ship";
        shipCount += 1;
      }
    }
    return board;
  }
