export default function clickReducer(
    state = 0, action
) {
    if (action.type === 'boardClick') {
        const value = state[action.x][action.y]
        if (value === "X") {
            return state + 1;
        }

    }
    if (action.type === 'RESET') {
        return 0;
    }
    return state;
}
